import React from 'react';
// Assuming you have images for each service in your assets folder
import transportationImg from '../assets/transportation.jpg';
import mealPreparationImg from '../assets/meal-preparation.jpg';
import medicationRemindersImg from '../assets/medication-reminders.jpg';
import homeCareImg from '../assets/home-care.jpg';

function ServicesSection() {
  return (
    <section id="services" className="py-12 bg-white">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-8">Services</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
          {/* Service Item */}
          <div className="service-item">
            <img src={transportationImg} alt="Transportation" className="w-full h-64 object-cover rounded-lg shadow-md"/>
            <h3 className="mt-4 text-xl font-semibold text-gray-800">Transportation</h3>
            <p className="mt-2 text-gray-600">Helping you get to where you need to be.</p>
          </div>
          {/* Repeat for other services */}
          <div className="service-item">
            <img src={mealPreparationImg} alt="Meal Preparation" className="w-full h-64 object-cover rounded-lg shadow-md"/>
            <h3 className="mt-4 text-xl font-semibold text-gray-800">Meal Preparation</h3>
            <p className="mt-2 text-gray-600">Nutritious meals planned and prepared for you.</p>
          </div>
          <div className="service-item">
            <img src={medicationRemindersImg} alt="Medication Reminders" className="w-full h-64 object-cover rounded-lg shadow-md"/>
            <h3 className="mt-4 text-xl font-semibold text-gray-800">Medication Reminders</h3>
            <p className="mt-2 text-gray-600">Ensuring your health stays on track.</p>
          </div>
          <div className="service-item">
            <img src={homeCareImg} alt="Home Care" className="w-full h-64 object-cover rounded-lg shadow-md"/>
            <h3 className="mt-4 text-xl font-semibold text-gray-800">Home Care</h3>
            <p className="mt-2 text-gray-600">Comprehensive home care services for your comfort.</p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default ServicesSection;
