import React, { useState } from 'react';
// Assuming you have images for each service in your assets folder
import homeHealthImg from '../assets/meal-preparation.jpg';
import homeCareImg from '../assets/meal-preparation.jpg';
import legal from '../assets/meal-preparation.jpg';
import financialPlanning from '../assets/meal-preparation.jpg';
// Add other imports for your images...

const advisingServices = [
  { name: "Home Health", img: homeHealthImg },
  { name: "Home Care", img: homeCareImg },
  // Add other advising services here...
];

const otherServices = [
  { name: "Legal", img: legal },
  { name: "Financial Planning", img: financialPlanning },
  // Add other services here...
];

function ServicesSection() {
  const [showAdvisingServices, setShowAdvisingServices] = useState(false);

  return (
    <section id="services" className="py-12 bg-white text-center">
      <div className="container mx-auto px-4">
        <div className="mb-8">
          <h2 className="text-3xl font-bold text-gray-800">Advising</h2>
          <button 
            className="mt-4 bg-blue-500 text-white font-bold py-2 px-4 rounded"
            onClick={() => setShowAdvisingServices(!showAdvisingServices)}
          >
            Click for More
          </button>
        </div>
        
        {showAdvisingServices && (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4 mb-8">
            {advisingServices.map((service, index) => (
              <div key={index} className="relative">
                <img src={service.img} alt={service.name} className="w-32 h-32 mx-auto object-cover rounded-full shadow-lg"/>
                <p className="absolute inset-0 flex items-center justify-center font-bold text-white">{service.name}</p>
              </div>
            ))}
          </div>
        )}
        
        <div>
          <h3 className="text-2xl font-bold text-gray-800">Other Services</h3>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4 mt-4">
            {otherServices.map((service, index) => (
              <div key={index} className="relative">
                <img src={service.img} alt={service.name} className="w-32 h-32 mx-auto object-cover rounded-full shadow-lg"/>
                <p className="absolute inset-0 flex items-center justify-center font-bold text-white">{service.name}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

export default ServicesSection;
