import React from 'react';
import { Link } from 'react-router-dom';
import heroBackground from '../assets/hero-background.jpg'; // Ensure you have an image at this path

function HeroSection() {
  return (
    <div className="relative bg-cover bg-center h-128 text-white" style={{ backgroundImage: `url(${heroBackground})` }}>
      <div className="absolute inset-0 bg-black opacity-50"></div> {/* Overlay */}
      <div className="container mx-auto flex flex-col items-center justify-center h-full px-4 md:px-0">
        <div className="text-center">
          <h1 className="text-4xl md:text-5xl xl:text-6xl font-bold leading-tight">Empowering Seniors to Live Independently</h1>
          <p className="mt-4 md:mt-6 text-lg md:text-xl xl:text-2xl font-light">Explore our services tailored for a vibrant senior life</p>
          <Link to="/about" className="mt-8 md:mt-10 inline-block bg-yellow-500 text-black font-medium py-3 px-6 text-lg rounded-lg hover:bg-yellow-600 transition-colors duration-300">Learn More</Link>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
