import React, { useState } from 'react';

// Example image imports, replace with your actual paths
import homeHealthImg from '../assets/meal-preparation.jpg';
import homeCareImg from '../assets/meal-preparation.jpg';
import legalImg from '../assets/meal-preparation.jpg';
import financialPlanningImg from '../assets/meal-preparation.jpg';
// Add other imports for your images...

const advisingServices = [
  { name: "Home Health", img: homeHealthImg },
  { name: "Home Care", img: homeCareImg },
  // Add other advising services here...
];

const otherServices = [
  { name: "Legal", img: legalImg },
  { name: "Financial Planning", img: financialPlanningImg },
  // Add other services here...
];

function ServicesSection() {
  const [showAdvising, setShowAdvising] = useState(false);

  return (
    <section id="services" className="py-12 bg-white">
      <div className="container mx-auto px-4 text-center">
        
        {/* Advising Services Section */}
        <div className="mb-8">
          <h2 className="text-3xl font-bold text-gray-800">Advising Services</h2>
          <button 
            className="mt-4 bg-yellow-500 text-white font-bold py-2 px-4 rounded hover:bg-yellow-500 transition duration-300"
            onClick={() => setShowAdvising(!showAdvising)}
          >
            {showAdvising ? 'Show Less' : 'Learn More'}
          </button>
        </div>
        {showAdvising && (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6 mb-12">
            {advisingServices.map((service, index) => (
              <div key={index} className="flex flex-col items-center">
                <img src={service.img} alt={service.name} className="w-48 h-48 object-cover rounded-full shadow-lg mb-2"/>
                <p className="font-bold text-lg">{service.name}</p>
              </div>
            ))}
          </div>
        )}

        {/* Other Services Section */}
        <div>
          <h3 className="text-2xl font-bold text-gray-800 mb-8">Other Services</h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
            {otherServices.map((service, index) => (
              <div key={index} className="flex flex-col items-center">
                <img src={service.img} alt={service.name} className="w-48 h-48 object-cover rounded-full shadow-lg mb-2"/>
                <p className="font-bold text-lg">{service.name}</p>
              </div>
            ))}
          </div>
        </div>

      </div>
    </section>
  );
}

export default ServicesSection;
