import React from 'react';
import transportationImg from '../assets/transportation.jpg';
import mealPreparationImg from '../assets/meal-preparation.jpg';
import medicationRemindersImg from '../assets/medication-reminders.jpg';
import homeCareImg from '../assets/home-care.jpg';

function ServicesSection() {
  return (
    <section id="services" className="py-12 bg-white text-center">
      <div className="container mx-auto px-4">
        <h2 className="services-header text-3xl font-bold text-gray-800 mb-8">Services</h2>
        <div className="services-container grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6 justify-items-center">
          {/* Service Item: Transportation */}
          <div className="service-item flex flex-col items-center justify-center">
            <img src={transportationImg} alt="Transportation" className="w-72 h-72 rounded-full object-cover mb-2" />
            <h3 className="text-xl font-semibold text-gray-800">Transportation</h3>
            <p className="text-gray-600">Helping you get to where you need to be.</p>
          </div>

          {/* Service Item: Meal Preparation */}
          <div className="service-item flex flex-col items-center justify-center">
            <img src={mealPreparationImg} alt="Meal Preparation" className="w-72 h-72 rounded-full object-cover mb-2" />
            <h3 className="text-xl font-semibold text-gray-800">Meal Preparation</h3>
            <p className="text-gray-600">Nutritious meals planned and prepared for you.</p>
          </div>

          {/* Service Item: Medication Reminders */}
          <div className="service-item flex flex-col items-center justify-center">
            <img src={medicationRemindersImg} alt="Medication Reminders" className="w-72 h-72 rounded-full object-cover mb-2" />
            <h3 className="text-xl font-semibold text-gray-800">Medication Reminders</h3>
            <p className="text-gray-600">Ensuring your health stays on track.</p>
          </div>

          {/* Service Item: Home Care */}
          <div className="service-item flex flex-col items-center justify-center">
            <img src={homeCareImg} alt="Home Care" className="w-72 h-72 rounded-full object-cover mb-2" />
            <h3 className="text-xl font-semibold text-gray-800">Home Care</h3>
            <p className="text-gray-600">Comprehensive home care services for your comfort.</p>
          </div>
        </div>
      </div>
      <style jsx>{`
        /* Custom CSS */
        .services-header {
          font-size: 2em;
          color: black;
          margin-bottom: 30px;
        }
        .services-container {
          display: grid;
          grid-template-columns: repeat(4, 1fr);
          gap: 20px;
          justify-items: center;
          padding: 0 5%;
        }
        .service-item img {
          width: 300px; /* Adjust based on your preference */
          height: 300px;
          border-radius: 50%;
          object-fit: cover;
          margin-bottom: 10px;
        }
        .service-item p {
          position: absolute;
          bottom: 50%;
          left: 50%;
          transform: translate(-50%, 50%);
          color: black;
          font-weight: bold;
          text-shadow: 2px 2px 4px rgba(255, 255, 255, 0.7);
          width: calc(100% - 20px);
          text-align: center;
          background-color: rgba(255, 215, 0, 0.75);
          border-radius: 20px;
          padding: 5px;
        }
      `}</style>
    </section>
  );
}

export default ServicesSection;
