import React from 'react';
import { Link } from 'react-router-dom';

function ContactInvitation() {
    return (
        <div style={{
            background: 'black', // Soft background color
            padding: '40px 20px',
            textAlign: 'center',
            borderRadius: '8px', // Slightly rounded corners for a softer look
            
            fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
            color: '#333',
            margin: '20px',
            maxWidth: '800px',
            marginLeft: 'auto',
            marginRight: 'auto'
        }}>
            <h2 style={{
                fontSize: '24px',
                color: '#2a2a2a',
                fontWeight: '600'
            }}>Schedule a Consultation</h2>
            <p style={{
                fontSize: '18px',
                margin: '20px auto 30px',
                maxWidth: '600px',
                lineHeight: '1.6'
            }}>
                Ready to elevate your commercial space with professional photography? Please contact me to schedule a personal consultation and discuss pricing details tailored to your specific needs.
            </p>
            <Link to="/contact" style={{
                display: 'inline-block',
                marginTop: '20px',
                padding: '10px 20px',
                fontSize: '16px',
                color: 'black',
                backgroundColor: 'transparent',
                textDecoration: 'none',
                borderRadius: '5px',
                border: '2px solid black',
                boxShadow: '0 0 0 2px white'
            }}>
                Contact Me
            </Link>
        </div>
    );
}

export default ContactInvitation;
