import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion'; // Import motion from Framer Motion
import heroBackground from '../assets/hero-background.jpg'; // Ensure you have an image at this path
import '..styles/herosection.css'; // Import the CSS file for styling

function HeroSection() {
  const text = "Gramps of Tennessee".split("");

  useEffect(() => {
    text.forEach((_, i) => {
      setTimeout(() => {
        const shineAnimation = document.getElementById(`letter-${i}`);
        if (shineAnimation) {
          shineAnimation.classList.add('shine');
        }
      }, i * 100);
    });
  }, []);

  return (
    <div className="relative w-full bg-cover bg-center bg-no-repeat text-white" style={{ backgroundImage: `linear-gradient(rgba(0,0,0,0.6), rgba(0,0,0,0.6)), url(${heroBackground})`, height: '60vh' }}>
      <div className="container mx-auto flex flex-col items-center justify-center h-full">
        <div className="text-center px-4 md:px-0">
          {text.map((letter, i) => (
            <motion.span
              id={`letter-${i}`}
              className="shine-text"
              key={i}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, delay: i * 0.1 }}
            >
              {letter}
            </motion.span>
          ))}
          <p className="mt-2 text-lg md:text-xl xl:text-2xl font-light text-gray-300">Ensuring silver linings of golden years</p>
          <Link to="/about" className="mt-8 md:mt-10 inline-block bg-yellow-500 text-black font-medium py-3 px-6 text-lg rounded-lg transition duration-300 hover:bg-yellow-600 hover:text-white shadow-md">Learn More</Link>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
