import React from 'react';
import { Link } from 'react-router-dom';
import heroBackground from '../assets/hero-background.jpg'; // Ensure you have an image at this path

function HeroSection() {
  return (
    <div className="relative w-full bg-cover bg-center bg-no-repeat text-white" style={{ backgroundImage: `url(${heroBackground})`, height: '50vh' }}>
      {/* Overlay to make the image a bit faded and enhance text visibility */}
      <div className="absolute inset-0 bg-black bg-opacity-40"></div>
      <div className="container mx-auto flex flex-col items-center justify-center h-full">
        <div className="text-center px-4 md:px-0">
          {/* Main Title */}
          <h1 className="text-4xl md:text-5xl xl:text-6xl font-bold leading-tight text-yellow-400 shadow-md">Gramps of Tennessee</h1>
          {/* Subtitle */}
          <p className="mt-2 text-lg md:text-xl xl:text-2xl font-light text-gray-300 shadow-lg">Ensuring silver linings of golden years</p>
          {/* Learn More Button */}
          <Link to="/about" className="mt-8 md:mt-10 inline-block bg-yellow-500 text-black font-medium py-3 px-6 text-lg rounded-lg hover:bg-yellow-600 transition-colors duration-300 shadow-md">Learn More</Link>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
