import React from 'react';
import { Link } from 'react-router-dom';
import heroBackground from '../assets/hero-background.jpg'; // Make sure the path to your image is correct

function HeroSection() {
  return (
    <div className="relative bg-no-repeat bg-center bg-contain h-screen text-white flex items-center justify-center" style={{ backgroundImage: `url(${heroBackground})` }}>
      {/* Adjust the size of the background with "bg-contain" and ensure it does not repeat with "bg-no-repeat". */}
      <div className="text-center">
        <h1 className="text-4xl md:text-5xl xl:text-6xl font-bold leading-tight">Empowering Seniors to Live Independently</h1>
        <p className="mt-4 md:mt-6 text-lg md:text-xl xl:text-2xl font-light">Explore our services tailored for a vibrant senior life</p>
        <Link to="/about" className="mt-8 md:mt-10 inline-block bg-[#FFD700] text-black font-medium py-3 px-6 text-lg rounded-lg hover:bg-[#FFC300] transition-colors duration-300">Learn More</Link>
      </div>
    </div>
  );
}

export default HeroSection;
