import React, { useState } from 'react';
import Gallery from 'react-photo-gallery';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css'; // Import Lightbox styles
import 'react-lazy-load-image-component/src/effects/blur.css';
import 'tailwindcss/tailwind.css'; // Import Tailwind CSS

const photos = [
    { src: './assets/10911 Dry Stack-31.jpg', width: 6172, height: 4115 },
    { src: './assets/927 Belle Oaks-10.jpg', width: 6192, height: 4128 },
    { src: './assets/Congress-13-X3.jpg', width: 1600, height: 1068 },
    { src: './assets/Congress-4-X3.jpg', width: 1600, height: 1068 },
    { src: './assets/Elevator lobby Ross ave-1-X3.jpg', width: 1600, height: 1067 },
    { src: './assets/FP 4 large conf jpg-X3-2.jpg', width: 1600, height: 1068 },
    { src: './assets/FP 4 large conf jpg-X3.jpg', width: 1600, height: 1068 },
    { src: './assets/Heifer-6.jpg', width: 5504, height: 8256 },
    { src: './assets/high rise-11.jpg', width: 4959, height: 7439 },
    { src: './assets/i-CPnVpmd-4K.jpg', width: 3840, height: 2563 },
    { src: './assets/i-hmPbjtG-4K.jpg', width: 3840, height: 2563 },
    { src: './assets/i-PVzgVvr-X3.jpg', width: 1600, height: 1068 },
    { src: './assets/i-thqsFzg-X3.jpg', width: 1600, height: 1068 },
    { src: './assets/i-WNkLZFq-4K.jpg', width: 3840, height: 2563 },
    { src: './assets/Paris Paul Cezanne-22.jpg', width: 4077, height: 4077 },
    { src: './assets/Paris-7-X3.jpg', width: 1600, height: 1068 },
    { src: './assets/Preston-1-X2.jpg', width: 1280, height: 854 },
    { src: './assets/Spring Mountain-8.jpg', width: 3191, height: 2127 },
    { src: './assets/St Paul-53.jpg', width: 5504, height: 8256 },
    { src: './assets/Wells Fargo Fitness Center -11-X3.jpg', width: 1600, height: 1068 },
    { src: './assets/Wells Fargo Fitness Center -7-X3.jpg', width: 1600, height: 1068 },
    { src: './assets/Westover Hills -1.jpg', width: 6192, height: 4128 },
    { src: './assets/WF Glendale final - jpg-1.jpg', width: 5076, height: 3384 },
    { src: './assets/WF Glendale final - jpg-22.jpg', width: 5504, height: 5504 },
    { src: './assets/WF Glendale final - jpg-7.jpg', width: 7362, height: 4908 },
    { src: './assets/_DSC5194-Edit.jpg', width: 6032, height: 4021 }
];

function PhotoGallery() {
    const [currentImage, setCurrentImage] = useState(0);
    const [isOpen, setIsOpen] = useState(false);

    const openLightbox = (event, { photo, index }) => {
        event.preventDefault(); // Prevent default action, if necessary
        setCurrentImage(index);
        setIsOpen(true);
    };

    const closeLightbox = () => {
        setIsOpen(false);
    };

    const moveNext = () => {
        setCurrentImage((currentImage + 1) % photos.length);
    };

    const movePrev = () => {
        setCurrentImage((currentImage + photos.length - 1) % photos.length);
    };

    return (
        <div className="p-5">
            <Gallery photos={photos} renderImage={({ photo, index }) => (
                <LazyLoadImage
                    key={index}
                    src={photo.src}
                    alt={`Image ${index}`}
                    width="100%"
                    height="auto"
                    effect="blur"
                    onClick={(event) => openLightbox(event, { photo, index })}  // Pass event explicitly
                />
            )} />
            {isOpen && (
                <Lightbox
                    mainSrc={photos[currentImage].src.replace('-small', '')}
                    nextSrc={photos[(currentImage + 1) % photos.length].src.replace('-small', '')}
                    prevSrc={photos[(currentImage + photos.length - 1) % photos.length].src.replace('-small', '')}
                    onCloseRequest={closeLightbox}
                    onMovePrevRequest={movePrev}
                    onMoveNextRequest={moveNext}
                />
            )}
        </div>
    );
}

export default PhotoGallery;
