import React from 'react';
import Gallery from 'react-photo-gallery';
import 'tailwindcss/tailwind.css';  // Ensure Tailwind CSS is imported

const photos = [
    { src: './assets/logo.png', width: 4, height: 3, alt: 'Edited photograph', caption: 'Stunning Edit Showcase' },
    { src: './assets/927 Belle Oaks-10.jpg', width: 4, height: 3, alt: 'Property at 927 Belle Oaks', caption: '927 Belle Oaks Property' },
    { src: './assets/Paris Paul Cezanne-22.jpg', width: 4, height: 3, alt: 'Related to Paul Cezanne', caption: 'Paris Paul Cezanne View' },
    // Add more images as needed
];

function PhotoGallery() {
    // Custom render for each photo using Tailwind CSS for styling
    const imageRenderer = ({ index, left, top, key, photo }) => (
        <div key={key} className="relative" style={{ margin: "2px", position: "absolute", left, top }}>
            <img src={photo.src} alt={photo.alt} className="w-full h-auto object-cover transform transition duration-500 hover:scale-110" />
            <div className="absolute inset-0 bg-black bg-opacity-50 opacity-0 hover:opacity-100 duration-500 flex justify-center items-center">
                <span className="text-white text-center p-3">{photo.caption}</span>
            </div>
        </div>
    );

    return (
        <div className="p-5">
            <Gallery photos={photos} renderImage={imageRenderer} />
        </div>
    );
}

export default PhotoGallery;
