import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import heroBackground from '../assets/hero-background.jpg';
import '../styles/herosection.css';

function HeroSection() {
  const fullText = "Gramps of Tennessee";
  const [text, setText] = useState("");
  const [typingCompleted, setTypingCompleted] = useState(false);

  useEffect(() => {
    let index = 0;
    const timeoutId = setInterval(() => {
      setText((prev) => {
        if (index === fullText.length - 1) {
          clearInterval(timeoutId);
          setTypingCompleted(true); // Mark typing as completed
        }
        return prev + fullText[index++];
      });
    }, 150); // Adjust the speed of typing here

    return () => clearInterval(timeoutId); // Cleanup on component unmount
  }, [fullText]);

  return (
    <div className="relative w-full bg-cover bg-center bg-no-repeat text-white" style={{ backgroundImage: `linear-gradient(rgba(0,0,0,0.6), rgba(0,0,0,0.6)), url(${heroBackground})`, height: '60vh' }}>
      <div className="container mx-auto flex flex-col items-center justify-center h-full">
        <div className="text-center px-4 md:px-0">
          {/* Main Title with Typewriter Effect */}
          <div className={`font-game-of-thrones leading-tight text-yellow-400 shadow-md ${!typingCompleted ? 'typewriter' : ''}`}>
            <h1 className="text-2xl md:text-3xl xl:text-6xl">{text}</h1>
          </div>
          {/* Subtitle */}
          <p className="mt-2 text-xl md:text-xl xl:text-3xl font-light text-gray-300 shadow-lg">Ensuring silver linings of golden years</p>
          {/* Learn More Button */}
          <Link to="/about" className="mt-8 md:mt-10 inline-block bg-yellow-500 text-black font-medium py-3 px-6 text-lg rounded-lg transition duration-300 hover:bg-yellow-600 hover:text-white shadow-md">Learn More</Link>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
