import React from 'react';
import { Link } from 'react-router-dom';
import heroBackground from '../assets/hero-background.jpg'; // Ensure you have the correct image path

function HeroSection() {
  return (
    <div className="relative w-full bg-cover bg-center bg-no-repeat text-white" style={{ backgroundImage: `url(${heroBackground})`, height: '50vh' }}>
      <div className="absolute inset-0 bg-black bg-opacity-40"></div> {/* Overlay for image fading */}
      <div className="container mx-auto flex flex-col items-center justify-center h-full">
        <div className="text-center px-4 md:px-0">
          {/* Title in gold */}
          <h1 className="text-4xl md:text-5xl xl:text-6xl font-bold leading-tight" style={{ color: '#FFD700' }}>Gramps of Tennessee</h1>
          {/* Subtitle in silver */}
          <p className="mt-4 md:mt-6 text-lg md:text-xl xl:text-2xl font-light" style={{ color: '#C0C0C0' }}>Ensuring silver linings of golden years</p>
          <Link to="/about" className="mt-8 md:mt-10 inline-block bg-[#FFD700] text-black font-medium py-3 px-6 text-lg rounded-lg hover:bg-[#FFC300] transition-colors duration-300">Learn More</Link>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
