import React from 'react';
// Assuming you have images for each service in your assets folder
import homeHealthImg from '../assets/meal-preparation.jpg';
import homeCareImg from '../assets/meal-preparation.jpg';
import legal from '../assets/meal-preparation.jpg';
import financialPlanning from '../assets/meal-preparation.jpg';
// Add other imports for your images...

const advisingServices = [
  { name: "Home Health", img: homeHealthImg },
  { name: "Home Care", img: homeCareImg },
  // Add other advising services here...
];

const otherServices = [
  { name: "Legal", img: legal },
  { name: "Financial Planning", img: financialPlanning },
  // Add other services here...
];

function ServicesSection() {
  return (
    <section id="services" className="py-12 bg-white text-center">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-gray-800 mb-8">Services</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {advisingServices.concat(otherServices).map((service, index) => (
            <div key={index} className="relative group">
              <img src={service.img} alt={service.name} className="w-48 h-48 mx-auto object-cover rounded-full shadow-lg transition duration-300 ease-in-out group-hover:opacity-75"/>
              <div className="absolute inset-0 flex items-center justify-center">
                <p className="text-white font-bold text-lg bg-black bg-opacity-50 rounded-full py-2 px-4">{service.name}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default ServicesSection;
