import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Typewriter from 'typewriter-effect/dist/core';
import heroBackground from '../assets/hero-background.jpg'; // Ensure you have an image at this path

function HeroSection() {
  const [typewriterCompleted, setTypewriterCompleted] = useState(false);

  useEffect(() => {
    if (!typewriterCompleted) {
      const typewriter = new Typewriter('#typewriter', {
        strings: ['Gramps of Tennessee'],
        autoStart: true,
        delay: 100,
      });

      typewriter.stop().callFunction(() => {
        setTypewriterCompleted(true);
      });
    }
  }, [typewriterCompleted]);

  return (
    <div className="relative w-full bg-cover bg-center bg-no-repeat text-white" style={{ backgroundImage: `linear-gradient(rgba(0,0,0,0.6), rgba(0,0,0,0.6)), url(${heroBackground})`, height: '60vh' }}>
      <div className="container mx-auto flex flex-col items-center justify-center h-full">
        <div className="text-center px-4 md:px-0">
          <h1 id="typewriter" className={`text-5xl md:text-6xl xl:text-7xl font-bold leading-tight text-yellow-400 shadow-md ${typewriterCompleted ? 'opacity-100' : 'opacity-0'}`}>Gramps of Tennessee</h1>
          <p className="mt-2 text-lg md:text-xl xl:text-2xl font-light text-gray-300 shadow-lg">Ensuring silver linings of golden years</p>
          <Link to="/about" className="mt-8 md:mt-10 inline-block bg-yellow-500 text-black font-medium py-3 px-6 text-lg rounded-lg transition duration-300 hover:bg-yellow-600 hover:text-white shadow-md">Learn More</Link>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
